		GVL_Abox.uaAboxInterface.uaDigitalOut.Output_3_6 := FALSE;
		GVL_Abox.uaAboxInterface.uaDigitalOut.Output_3_7 := FALSE;
	END_IF;
END_IF

IF Enable THEN
	rTrigIn(CLK := ioStation.bSensor);

	IF rTrigIn.Q THEN
		diCounter := diCounter + 1;
		release := FALSE;
		stop := TRUE;
	END_IF
	IF ioStation.bButton THEN
		release := TRUE;
		stop := FALSE;
	END_IF
	ioStation.bLed := stop;

	tonResetCounter(IN := ioStation.bButton,
					PT := T#2S);

	IF tonResetCounter.Q THEN
		diCounter := 0;
	END_IF
END_IF

IF Enable THEN
	rTrigIn(CLK := ioStation.bSensor);

	IF rTrigIn.Q THEN
		diCounter := diCounter + 1;
		release := FALSE;
		stop := TRUE;
	END_IF
	IF ioStation.bButton THEN
		release := TRUE;
		stop := FALSE;
	END_IF
	ioStation.bLed := stop;

	tonResetCounter(IN := ioStation.bButton,
					PT := T#2S);

	IF tonResetCounter.Q THEN
		diCounter := 0;
	END_IF

	ioStation.bBuzzer := stop;
END_IF

